import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const BlogPostList = () => {
  const [blogPosts, setBlogPosts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/blogposts");
        setBlogPosts(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="container mx-auto px-4 mt-8">
      <h1 className="text-3xl font-bold text-blue-gray-800 text-center mb-8">
        Blog Posts
      </h1>

      <ul className="grid sm:grid-cols-2 gap-4">
        {blogPosts.map((post) => (
          <li
            key={post.id}
            className="shadow rounded overflow-hidden bg-white max-w-sm transition duration-300 hover:shadow-md hover:bg-gray-50"
          >
            <Link to={`/post/${post.id}`}>
              <img
                src="https://via.placeholder.com/150" // Replace with your image source
                alt={post.title}
                className="w-full h-48 object-cover"
              />
            </Link>
            <div className="p-4">
              <Link to={`/post/${post.id}`}>
                <h2 className="text-2xl font-bold text-blue-gray-800 hover:text-blue-gray-700 mb-2">
                  {post.title}
                </h2>
              </Link>
              <p className="text-gray-600 text-opacity-75 hover:text-opacity-100">
                {post.content}
              </p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BlogPostList;
