import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import BlogPostList from './components/BlogPostList';
import BlogPost from './components/BlogPost';
import NewBlogPost from './components/NewBlogPost';

// Header component
const Header = () => (
<header className="bg-gradient-to-r from-blue-800 to-blue-700 p-4 flex items-center justify-between">
  <Link to="/" className="text-white text-xl font-bold flex items-center">
    <svg className="w-8 mr-2 fill-current text-white" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path d="M10.707 2.293a1 1 0 010 1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414-1.414L12.586 8H2a1 1 0 110-2h10.586l-2.293-2.293zM8 14a1 1 0 100-2h4a1 1 0 100 2H8z" /></svg>
    Home
  </Link>
  <Link to="/new" className="text-white flex items-center">
    <svg className="w-6 mr-2 fill-current text-white" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path d="M5.586 8.757a4 4 0 107.828-5.59l2.828 2.828a1 1 0 11-1.414 1.414l-2.828-2.828zM8 16a4 4 0 100-8a4 4 0 010 8z" /></svg>
    New Blog Post
  </Link>
</header>
);

// App component
function App() {
  return (
    <Router>
      <div>
        <Header />
        <Routes>
          <Route path="/" element={<BlogPostList />} />
          <Route path="/post/:id" element={<BlogPost />} />
          <Route path="/new" element={<NewBlogPost />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
